import base64
from io import BytesIO
from PIL import Image
import pytest
import PIL.Image


def test_base64_to_image(
        base64_string: str = ""
):
    byte_stream = base64.b64decode(base64_string)

    image = Image.open(BytesIO(byte_stream))
    PIL.Image.Image.show(image)


